class Solution {
    public void setZeroes(int[][] matrix) {
        Queue<int[]> que=new LinkedList<>();
        int row=matrix.length;
        int col=matrix[0].length;
        for(int i=0;i<row;i++){
            for(int j=0;j<col;j++){
                if(matrix[i][j]==0){
                    que.add(new int[]{i,j});
                }
            }
        }

        while(!que.isEmpty()){
            int[] arr=que.poll();
            int r=arr[0];
            int c=arr[1];
            for(int i=r;i<row;i++){
                matrix[i][c]=0;
            }
            for(int i=r;i>=0;i--){
                matrix[i][c]=0;
            }
            for(int i=c;i<col;i++){
                matrix[r][i]=0;
            }
            for(int i=c;i>=0;i--){
                matrix[r][i]=0;
            }
        }
    }
}

