class Solution {
public:
    int thirdMax(vector<int>& nums) {
        int len = nums.size();
        if(len == 1){
            return nums[0];
        }

        unordered_set<int> uniques;
        for(int x : nums){
            uniques.insert(x);
        }

        vector<int> sortedUniques;

        for(int x : uniques){
            sortedUniques.push_back(x);
        }

        if(sortedUniques.size() == 1){
            return sortedUniques[0];
        }
        sort(sortedUniques.begin(), sortedUniques.end());
        if(sortedUniques.size() == 2){
            return sortedUniques[1];
        }
        return sortedUniques[sortedUniques.size()-3];
    }
};

