        int trueMax = max;
        remove(nums, max);

        for (int i=0; i<2; i++) {
            if(nums.empty()) return trueMax;
            max = getMax(nums);
            remove(nums, max);
        }
        return max;
    }

    int getMax(vector<int>& nums) {
        int max= INT_MIN;
        for (int num: nums) {
            if (num > max) max = num;
        }
        return max;
    }

    void remove(vector<int>& nums, int val) {
        for (int i=0; i<nums.size(); i++) {
            if (nums[i] == val){
                nums.erase(nums.begin() + i);
                i--;
            }
        }
    }
};

